mapper.select:
/* [AdminBrandApi_SQL.xml].[getBrandListCnt] */ SELECT count(*) as total FROM app_board_brand_test a LEFT OUTER JOIN app_image_file_info b ON a.brand_seq = b.board_seq AND b.board_type_cd = 'brdt001' AND b.image_type_cd = 'imgt001' LEFT OUTER JOIN J_ATTACHFILE_TEST c ON b.image_file_id = c.FILE_ID LEFT OUTER JOIN app_url_link_info_test d ON a.brand_seq = d.board_seq AND d.board_type_cd = 'brdt001' AND d.link_type_cd = 'lnkt001'
a.visible != 'D'
AND a.category_cd = #{categoryCd}
AND a.brand_name LIKE CONCAT ('%', #{searchKeyword},'%')

/* [AdminBrandApi_SQL.xml].[getBrandList] */ SELECT @ROWNUM := @ROWNUM + 1 AS RNUM , a.brand_seq as brandSeq , a.category_cd as categoryCd , (select code_name from app_code where code_id = a.category_cd) categoryName , a.menu_category_cd as menuCategoryCd , a.menu_category_nm as menuCategoryNm , a.brand_name as brandName , a.brand_description as brandDescription , a.visible as visible , a.creator as creator , (SELECT name from app_user_info where user_seq = creator) as creatorNm , DATE_FORMAT(a.create_time, '%Y.%m.%d') as createTime , a.updater as updater , (SELECT name from app_user_info where user_seq = updater) as updaterNm , DATE_FORMAT(a.update_time, '%Y.%m.%d') as updateTime , b.image_file_info_seq as imageFileInfoSeq , c.FILE_NAME as imageFileName , d.link_seq as linkSeq , d.link_address as linkAddress , d.visible as brandLinkVisible FROM ( SELECT @ROWNUM := 0) temp ,app_board_brand_test a LEFT OUTER JOIN app_image_file_info_test b ON a.brand_seq = b.board_seq AND b.board_type_cd = 'brdt001' AND b.image_type_cd = 'imgt001' LEFT OUTER JOIN J_ATTACHFILE_TEST c ON b.image_file_id = c.FILE_ID LEFT OUTER JOIN app_url_link_info_test d ON a.brand_seq = d.board_seq AND d.board_type_cd = 'brdt001' AND d.link_type_cd = 'lnkt001'
a.visible != 'D' AND a.brand_seq = #{brandSeq}
AND a.category_cd = #{categoryCd}
AND a.brand_name LIKE CONCAT ('%', #{searchKeyword},'%')
ORDER BY brand_seq DESC LIMIT #{startPage}, #{size}

/* [AdminBrandApi_SQL.xml].[getSnsLinkList] */ SELECT A.code_name snsName , B.link_seq , B.board_type_cd , B.board_seq , IFNULL(B.link_type_cd,A.code_id) as linkTypeCd , B.link_address , B.sort_order , B.visible , B.creator , B.create_time , B.updater , B.update_time , C.FILE_ID , C.FILE_NAME , C.FILE_PATH FROM app_code A LEFT OUTER JOIN app_url_link_info_test B ON A.code_id = B.link_type_cd AND B.board_seq = #{boardSeq} AND B.board_type_cd = #{boardTypeCd} AND B.link_type_cd NOT IN ('lnkt001', 'lnkt002') LEFT OUTER JOIN J_ATTACHFILE_TEST C ON A.property1 = C.FILE_ID WHERE A.parent_cd = 'lnkt002'
AND B.visible = #{visible}
ORDER BY A.sort_order

mapper.insert:
