, create_time
, updater
, update_time)
VALUES ( #{categoryCd}
	   , #{menuCategoryCd}
	   , #{menuCategoryNm}
	   , #{brandName}
	   , #{brandDescription}
	   , #{visible}
	   , #{updater}
	   , now()
	   , #{updater}
	   , now())
