mapper.select:
/* [AdminCategoryApi_SQL.xml].[getCategoryListCnt] */
SELECT count(*) totCnt
FROM app_code_test a
WHERE parent_cd = #{parentCd}

	AND visible = 'Y'


	AND a.code_id = #{codeId}

/* [AdminCategoryApi_SQL.xml].[getCategoryList] */ SELECT a.code_id as codeId , a.code_name as codeName , a.code_value as codeValue , a.description as codeDesc , a.parent_cd as parentCd , a.property1 as property1 , a.property2 as property2 , a.property3 as property3 , a.sort_order as sortOrder , a.visible as visible , a.creator as creator , (select name from app_user_info where user_seq = 1) as creatorName , DATE_FORMAT(a.create_time, '%Y-%m-%d') as createTime , a.updater as updater , DATE_FORMAT(a.update_time, '%Y-%m-%d') as updateTime , (select FILE_NAME from J_ATTACHFILE_TEST where file_id = a.property1) as fileName , (select FILE_PATH from J_ATTACHFILE_TEST where file_id = a.property1) as filePath , (select concat('http://tjinternational.kr/file/image/',file_mask) from J_ATTACHFILE_TEST where file_id = a.property1) AS newFilePath FROM app_code_test a WHERE parent_cd = #{parentCd}
AND visible = 'Y'
AND a.code_id = #{codeId}
ORDER BY sortOrder DESC
ORDER BY sortOrder
LIMIT #{startPage}, #{size}

mapper.insert:
/* [AdminCategoryApi_SQL.xml].[insertCategory] */
INSERT INTO app_code_test
( code_id
, code_name
, code_value
, description
, parent_cd
, property1
, property2
, property3
, sort_order
, visible
, creator
, create_time
, updater
, update_time)
VALUES ((select concat('ct', lpad(right(code_id, 3) + 1, 3, '0'))
		 from app_code_test a
		 where parent_cd = 'ct000'
		 order by code_id desc limit 1)
	 , #{codeName}
	 , #{codeValue}
	 , #{description}
	 , #{parentCd}
	 , #{property1}
	 , #{property2}
	 , #{property3}
	 , (select sort_order + 1 from app_code_test b where parent_cd = 'ct000' order by code_id desc limit 1)
	 , 'Y'
	 , #{updater}
	 , NOW()
	 , #{updater}
	 , NOW()
	)
