mapper.select:
/*[AdminRecruitMgmt_SQL.xml].[getRecruitCnt] */ SELECT count(*) total FROM app_board_recruit
AND visible != 'D'
AND recruit_title LIKE CONCAT ('%', #{searchKeyword},'%')

/*[AdminPrMgmt_SQL.xml].[selectNewsList] */ SELECT recruit_seq, recruit_title, recruit_description, DATE_FORMAT(opening_date, '%Y-%m-%d') as openingDate, DATE_FORMAT(closing_date, '%Y-%m-%d') as closingDate, visible, creator, case when NOW() BETWEEN opening_date AND DATE_ADD(closing_date, INTERVAL 1 DAY) then ' ELSE 'END as recruitDateYn, create_time, updater, update_time FROM app_board_recruit
AND visible != 'D'
AND recruit_seq = #{recruitSeq}
AND recruit_title LIKE CONCAT ('%', #{searchKeyword},'%')
AND NOW() BETWEEN opening_date AND DATE_ADD(closing_date, INTERVAL 1 DAY)
AND !(NOW() BETWEEN opening_date AND DATE_ADD(closing_date, INTERVAL 1 DAY))
ORDER BY recruitDateYn DESC, recruit_seq DESC LIMIT #{startPage}, #{size}
