mapper.select:
/*[AdminLoginApi_SQL.xml].[getUserId] */
SELECT user_id as id, password
FROM app_user_info
WHERE user_id = #{id}

/*[AdminLoginApi_SQL.xml].[adminLogin] */
SELECT PASSWORD
FROM app_user_info
WHERE USER_ID = #{userId}

/*[AdminLoginApi_SQL.xml].[selectAdminSeq] */
SELECT USER_SEQ as userSeq,
	   name
FROM app_user_info
WHERE USER_ID = #{userId}
