mapper.select:
/*[FrontNewsApi_SQL.xml].[frontNewsList] */ SELECT A.news_seq, A.news_title, A.news_description, A.visible, A.creator, (SELECT name from app_user_info where user_seq = creator) as creatorNm, DATE_FORMAT(A.create_time, '%Y.%m.%d') as create_time, A.updater, (SELECT name from app_user_info where user_seq = updater) as updaterNm, DATE_FORMAT(A.update_time, '%Y.%m.%d') as update_time, file_id, file_mask, file_path FROM app_board_news_test A left OUTER join (select * from app_image_file_info_test where board_type_cd = 'brdt002' AND visible = 'Y') B ON A.news_seq = B.board_seq left outer join J_ATTACHFILE_TEST C on B.image_file_id = C.file_id WHERE A.visible = 'Y'
AND news_title LIKE CONCAT ('%', #{searchKeyword},'%')
AND news_description LIKE CONCAT ('%', #{searchKeyword},'%')
ORDER BY news_seq DESC LIMIT #{startPage}, #{size}

/*[FrontNewsApi_SQL.xml].[frontNewsListCnt] */ SELECT COUNT(*) AS TOT FROM app_board_news_test where visible = 'Y'
AND news_title LIKE CONCAT ('%', #{searchKeyword},'%')
AND news_description LIKE CONCAT ('%', #{searchKeyword},'%')

/*[FrontNewsApi_SQL.xml].[frontSnsLinkList] */
SELECT *
FROM app_url_link_info_test
WHERE board_type_cd = 'brdt001'
  AND board_seq = #{newsSeq}

/*[FrontNewsApi_SQL.xml].[frontNewsPrevIdx] */
SELECT max(news_seq)
FROM app_board_news_test
WHERE news_seq < #{newsSeq}
  AND visible = 'Y'

/*[FrontNewsApi_SQL.xml].[frontNewsNextIdx] */
SELECT min(news_seq)
FROM app_board_news_test
WHERE news_seq > #{newsSeq}
  AND visible = 'Y'
