mapper.select:
/*[AdminPrMgmt_SQL.xml].[selectNewsList] */
SELECT news_seq, news_title, news_description, visible, creator, (SELECT name from app_user_info where user_seq = creator) as creatorNm, DATE_FORMAT(create_time, '%Y.%m.%d') as create_time, updater, (SELECT name from app_user_info where user_seq = updater) as updaterNm, DATE_FORMAT(update_time, '%Y.%m.%d') as update_time FROM app_board_news_test WHERE visible != 'D'
AND news_title LIKE CONCAT ('%', #{searchKeyword},'%')
AND news_description LIKE CONCAT ('%', #{searchKeyword},'%')
ORDER BY news_seq DESC LIMIT #{startPage}, #{size}
